import React, { useState } from 'react';
import './ExpenseForm.css';

function ExpenseForm(){

    const [enteredTitle, setEnteredTitle] = useState('')
    const [enteredAmount, setEnteredAmount] = useState('')
    const [enteredDate, setEnteredDate] = useState('')

    console.log("enteredTitle", enteredTitle)

    const titleChangeHandler = (event) => {
        setEnteredTitle(event.target.value)
    }

    const amountChangeHandler = (e) => {
        setEnteredAmount(e.target.value)
    }

    const dateChangeHandler = (e) => {
        setEnteredDate(e.target.value)
    }

    const formData =[
        { className: 'new-expense__control', label: 'Title', inputType: 'text', onChangeHandler: {titleChangeHandler} },
        { className: 'new-expense__control', label: 'Amount', inputType: 'number',onChangeHandler: {amountChangeHandler} },
        { className: 'new-expense__control', label: 'Date', inputType: 'date', onChangeHandler: {dateChangeHandler} },
    ];

    return (
        <form>
            <div className="new-expense__controls">
                {formData.map((item, index) =>
                        <div key={index} className={item.className}>
                            <label>{item.label}</label>
                            <input type={item.inputType} onChange={item.onChange}/>
                        </div>)
                }
            </div>
                <div className="new-expense__actions">
                    <button type="submit">Add Expense</button>
                </div>
        </form>
    )

}

export default ExpenseForm;
