import React, { useState } from 'react'
import ExpenseItem from "./components/ExpenseItem";
import NewExpense from "./components/NewExpense";

let data = [
  { title: 'Car Insurance', amount: 294.67, date: new Date(2021, 2, 18) },
  { title: 'TV', amount: 580.50, date: new Date(2022, 1, 23) },
  { title: 'Booze', amount: 340.30, date: new Date(2022, 10, 15) },
]

function App() {
  const [expenses, setExpenses] = useState(data);

  const addExpenseHandler = (expense) => {
    setExpenses((prevState) => { return [expense, ...prevState] })
  }

  return (
    <div className="title">
      <h2>Let's get started!</h2>
      <NewExpense onAddExpense={addExpenseHandler} />
      <div key="expenses">
        {
          expenses.map((item, index) => <ExpenseItem key={index} title={item.title} amount={item.amount} date={item.date} />
          )
        }
      </div>
    </div>
  );
}

export default App;
